import React, {Component} from 'react';
import Fab from '@material-ui/core/Fab';
// import ArrowIcon from 'material-ui/svg-icons/hardware/keyboard-arrow-down';
import './header.css';
const scrollTo = require('scroll-to');


class HeaderComponent extends Component {
    constructor(props) {
        super(props);
        this.state = {
          height: props.height,
          scrollTo: props.height
        };
    }

    updateDimensions() {
      this.setState({
        height:window.innerHeight+'px',
        scrollTo: window.innerHeight
      });
    }

    async componentDidMount() {
      window.addEventListener("resize", this.updateDimensions.bind(this));
      this.updateDimensions();
    }

    componentWillUnmount() {
      window.removeEventListener("resize", this.updateDimensions.bind(this));
    }

    onScrollToIntro() {
        scrollTo(0, this.state.scrollTo, {
            ease: 'out-bounce',
            duration: 2000
        });
    }

    render() {
        return (
          <div className="header-wrapper" style={{"height": this.state.height}}>
            <div className="header">
                <h3>Sara Steiert</h3>
                <h6>Junior Technology Consultant</h6>
            </div>
            <div className="scroll-down-btn">
              <Fab mini={true} backgroundColor="#a80202" onClick={this.onScrollToIntro.bind(this)}>
                {/* <ArrowIcon /> */}
              </Fab>
            </div>

          </div>
        );
    }
}

export default HeaderComponent;
